mapper.select:
SELECT



FROM qiye  a



        and a.id in

        #{item}



        and a.username like CONCAT('%',#{params.username},'%')


        and a.password like CONCAT('%',#{params.password},'%')


        and a.qiye_name like CONCAT('%',#{params.qiyeName},'%')


        and a.qiye_types = #{params.qiyeTypes}


        and a.qiye_phone like CONCAT('%',#{params.qiyePhone},'%')


        and a.qiye_email like CONCAT('%',#{params.qiyeEmail},'%')


        and a.qiye_guimo like CONCAT('%',#{params.qiyeGuimo},'%')


          and UNIX_TIMESTAMP(a.qiye_chenglishijian_time) >= UNIX_TIMESTAMP(#{params.qiyeChenglishijianTimeStart}) 


          and UNIX_TIMESTAMP(a.qiye_chenglishijian_time) <= UNIX_TIMESTAMP(#{params.qiyeChenglishijianTimeEnd}) 


        and a.qiye_chenglishijian_time = #{params.qiyeChenglishijianTime}


        and a.qiye_content like CONCAT('%',#{params.qiyeContent},'%')


          and a.qiye_delete >= #{params.qiyeDeleteStart}   


          and a.qiye_delete <= #{params.qiyeDeleteEnd}   


        and a.qiye_delete = #{params.qiyeDelete}



order by a.${params.sort} ${params.order}
