mapper.select:
SELECT
,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_zhuangtai_types as yonghuZhuangtaiTypes ,zhaopin.qiye_id as zhaopinQiyeId ,zhaopin.zhaopin_name as zhaopinName ,zhaopin.zhaopin_photo as zhaopinPhoto ,zhaopin.zhaopin_daiyu as zhaopinDaiyu ,zhaopin.zhaopin_address as zhaopinAddress ,zhaopin.lianxiren_name as lianxirenName ,zhaopin.zhaopin_phone as zhaopinPhone ,zhaopin.zhaopin_types as zhaopinTypes ,zhaopin.zhaopin_renshu_number as zhaopinRenshuNumber ,zhaopin.shangxia_types as shangxiaTypes ,zhaopin.zhaopin_content as zhaopinContent ,zhaopin.zhaopin_yesno_types as zhaopinYesnoTypes ,zhaopin.zhaopin_yesno_text as zhaopinYesnoText ,zhaopin.zhaopin_shenhe_time as zhaopinShenheTime
FROM zhaopin_liuyan a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id left JOIN zhaopin zhaopin ON a.zhaopin_id = zhaopin.id
and a.id in
#{item}
and ( a.zhaopin_id = #{params.zhaopinId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.zhaopin_liuyan_text like CONCAT('%',#{params.zhaopinLiuyanText},'%')
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.update_time = #{params.updateTime}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_zhuangtai_types = #{params.yonghuZhuangtaiTypes}
and a.zhaopin_id IS NOT NULL
and zhaopin.qiye_id = #{params.qiyeId}
and zhaopin.zhaopin_name like CONCAT('%',#{params.zhaopinName},'%')
and zhaopin.zhaopin_daiyu like CONCAT('%',#{params.zhaopinDaiyu},'%')
and zhaopin.zhaopin_address like CONCAT('%',#{params.zhaopinAddress},'%')
and zhaopin.lianxiren_name like CONCAT('%',#{params.lianxirenName},'%')
and zhaopin.zhaopin_phone like CONCAT('%',#{params.zhaopinPhone},'%')
and zhaopin.zhaopin_types = #{params.zhaopinTypes}
and zhaopin.zhaopin_renshu_number >= #{params.zhaopinRenshuNumberStart}
and zhaopin.zhaopin_renshu_number <= #{params.zhaopinRenshuNumberEnd}
and zhaopin.zhaopin_renshu_number = #{params.zhaopinRenshuNumber}
and zhaopin.shangxia_types = #{params.shangxiaTypes}
and zhaopin.zhaopin_content like CONCAT('%',#{params.zhaopinContent},'%')
and zhaopin.zhaopin_yesno_types = #{params.zhaopinYesnoTypes}
and zhaopin.zhaopin_yesno_text like CONCAT('%',#{params.zhaopinYesnoText},'%')
and UNIX_TIMESTAMP(zhaopin.zhaopin_shenhe_time) >= UNIX_TIMESTAMP(#{params.zhaopinShenheTimeStart})
and UNIX_TIMESTAMP(zhaopin.zhaopin_shenhe_time) <= UNIX_TIMESTAMP(#{params.zhaopinShenheTimeEnd})
and a.zhaopin_shenhe_time = #{params.zhaopinShenheTime}
order by a.${params.sort} ${params.order}
