mapper.select:
SELECT
,jianli.yonghu_id as jianliYonghuId ,jianli.jianli_uuid_number as jianliUuidNumber ,jianli.jianli_name as jianliName ,jianli.jianli_xingming as jianliXingming ,jianli.jianli_types as jianliTypes ,jianli.jianli_xinzi as jianliXinzi ,jianli.jianli_xueli as jianliXueli ,jianli.jianli_jingli as jianliJingli ,jianli.sex_types as sexTypes ,jianli.jianli_file as jianliFile ,jianli.jianli_phone as jianliPhone ,jianli.jianli_photo as jianliPhoto ,jianli.jianli_address as jianliAddress ,jianli.jiaoyu_text as jiaoyuText ,jianli.shixi_text as shixiText ,jianli.geren_text as gerenText ,zhaopin.qiye_id as zhaopinQiyeId ,zhaopin.zhaopin_name as zhaopinName ,zhaopin.zhaopin_photo as zhaopinPhoto ,zhaopin.zhaopin_daiyu as zhaopinDaiyu ,zhaopin.zhaopin_address as zhaopinAddress ,zhaopin.lianxiren_name as lianxirenName ,zhaopin.zhaopin_phone as zhaopinPhone ,zhaopin.zhaopin_types as zhaopinTypes ,zhaopin.zhaopin_renshu_number as zhaopinRenshuNumber ,zhaopin.shangxia_types as shangxiaTypes ,zhaopin.zhaopin_content as zhaopinContent ,zhaopin.zhaopin_yesno_types as zhaopinYesnoTypes ,zhaopin.zhaopin_yesno_text as zhaopinYesnoText ,zhaopin.zhaopin_shenhe_time as zhaopinShenheTime
FROM zhaopin_toudi a left JOIN jianli jianli ON a.jianli_id = jianli.id left JOIN zhaopin zhaopin ON a.zhaopin_id = zhaopin.id
and a.id in
#{item}
and ( a.jianli_id = #{params.jianliId} )
and ( a.zhaopin_id = #{params.zhaopinId} )
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and a.zhaopin_toudi_yesno_types = #{params.zhaopinToudiYesnoTypes}
and a.zhaopin_toudi_yesno_text like CONCAT('%',#{params.zhaopinToudiYesnoText},'%')
and UNIX_TIMESTAMP(a.zhaopin_toudi_shenhe_time) >= UNIX_TIMESTAMP(#{params.zhaopinToudiShenheTimeStart})
and UNIX_TIMESTAMP(a.zhaopin_toudi_shenhe_time) <= UNIX_TIMESTAMP(#{params.zhaopinToudiShenheTimeEnd})
and a.zhaopin_toudi_shenhe_time = #{params.zhaopinToudiShenheTime}
and a.zhaopin_toudi_jieguo_types = #{params.zhaopinToudiJieguoTypes}
and a.jianli_id IS NOT NULL
and jianli.yonghu_id = #{params.yonghuId}
and jianli.jianli_uuid_number like CONCAT('%',#{params.jianliUuidNumber},'%')
and jianli.jianli_name like CONCAT('%',#{params.jianliName},'%')
and jianli.jianli_xingming like CONCAT('%',#{params.jianliXingming},'%')
and jianli.jianli_types = #{params.jianliTypes}
and jianli.jianli_xinzi like CONCAT('%',#{params.jianliXinzi},'%')
and jianli.jianli_xueli like CONCAT('%',#{params.jianliXueli},'%')
and jianli.jianli_jingli like CONCAT('%',#{params.jianliJingli},'%')
and jianli.sex_types = #{params.sexTypes}
and jianli.jianli_phone like CONCAT('%',#{params.jianliPhone},'%')
and jianli.jianli_address like CONCAT('%',#{params.jianliAddress},'%')
and jianli.jiaoyu_text like CONCAT('%',#{params.jiaoyuText},'%')
and jianli.shixi_text like CONCAT('%',#{params.shixiText},'%')
and jianli.geren_text like CONCAT('%',#{params.gerenText},'%')
and a.zhaopin_id IS NOT NULL
and zhaopin.qiye_id = #{params.qiyeId}
and zhaopin.zhaopin_name like CONCAT('%',#{params.zhaopinName},'%')
and zhaopin.zhaopin_daiyu like CONCAT('%',#{params.zhaopinDaiyu},'%')
and zhaopin.zhaopin_address like CONCAT('%',#{params.zhaopinAddress},'%')
and zhaopin.lianxiren_name like CONCAT('%',#{params.lianxirenName},'%')
and zhaopin.zhaopin_phone like CONCAT('%',#{params.zhaopinPhone},'%')
and zhaopin.zhaopin_types = #{params.zhaopinTypes}
and zhaopin.zhaopin_renshu_number >= #{params.zhaopinRenshuNumberStart}
and zhaopin.zhaopin_renshu_number <= #{params.zhaopinRenshuNumberEnd}
and zhaopin.zhaopin_renshu_number = #{params.zhaopinRenshuNumber}
and zhaopin.shangxia_types = #{params.shangxiaTypes}
and zhaopin.zhaopin_content like CONCAT('%',#{params.zhaopinContent},'%')
and zhaopin.zhaopin_yesno_types = #{params.zhaopinYesnoTypes}
and zhaopin.zhaopin_yesno_text like CONCAT('%',#{params.zhaopinYesnoText},'%')
and UNIX_TIMESTAMP(zhaopin.zhaopin_shenhe_time) >= UNIX_TIMESTAMP(#{params.zhaopinShenheTimeStart})
and UNIX_TIMESTAMP(zhaopin.zhaopin_shenhe_time) <= UNIX_TIMESTAMP(#{params.zhaopinShenheTimeEnd})
and a.zhaopin_shenhe_time = #{params.zhaopinShenheTime}
order by a.${params.sort} ${params.order}
